import React, { useState } from 'react';
import axios from 'axios';
import useToken from '../../useToken';
import './SubmitBot.css'

interface leaderboardProps {
  name: string;
}

const SubmitBot = ({name}: leaderboardProps) => {
  const [code, setCode] = useState('');
  const [botName, setBotName] = useState('');
  const { getToken } = useToken();

  useEffect(() => {
    const setLeaderboardCall = async () => {
      await axios
      .post('http://localhost:3001/api/game/getGameBots', {
        gameName: name
      })
      .then(function (response) {
        setLeaderboard(response.data.bots.sort((bot1: Bot, bot2: Bot) => bot1.rank - bot2.rank));
      })
      .catch(function () {
        return "Failed get leaderboard";
      });
    };

    setLeaderboardCall();
  }, []);

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    let botId = "";
    await axios
      .post('http://localhost:3001/api/game/createGameBot', {
        botName: botName,
        userId: getToken(),
        gameName: name
      })
      .then(function (response) {
        botId = response.data.bot._id;
      })
      .catch(function () {
        return "Failed new bot";
      });

    await axios
      .post('http://localhost:3001/api/game/updateGameBot/tictactoe', {
        botId: botId,
        language: 'c++',
        sourceCode: code
      })
      .catch(function () {
        return "Failed to update bot";
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div id="textArea">
        <textarea
          value={code}
          onChange={(e) => setCode(e.target.value)}
          placeholder="Type code here..."
        ></textarea>
        <textarea
          value={botName}
          onChange={(e) => setBotName(e.target.value)}
          placeholder="Bot Name"
        ></textarea>
      </div>

      <button>Submit</button>

    </form>
  );
};

export default SubmitBot;
